css:
.btn {
  color: #444444;
  border-radius: 2px;
  border: none;
  text-shadow: none;
  height: 27px;
  line-height: 25px;
  padding: 0 10px;
  font-size: 13px;
  z-index: 0;
  background-color: #f4f4f4;
}
.btn:hover {
  background-color: #ffffff;
}
.btn.selected,
.btn.selected:hover {
  z-index: 1;
  background-color: #888888;
}
.btn.btn-xs,
.btn-group-xs > .btn {
  padding: 2.5px 5px;
  font-size: 11px;
  height: auto;
  line-height: 1.3em;
}
.btn.btn-sm,
.btn-group-sm > .btn {
  padding: 2.5px 5px;
  height: auto;
  line-height: 1.3em;
}
.btn.btn-lg,
.btn-group-lg > .btn {
  font-size: 15px;
  padding: 8px 12px;
  height: auto;
  line-height: 1.3em;
}
.btn-group > .btn {
  border-left: 1px solid rgba(0, 0, 0, 0.15);
  border-right: 1px solid rgba(0, 0, 0, 0.15);
}
.btn-group > .btn:first-child {
  border-left: none;
  border-top-left-radius: 2px;
  border-bottom-left-radius: 2px;
}
.btn-group > .btn:last-child,
.btn-group > .btn.selected:last-child,
.btn-group > .dropdown-toggle {
  border-right: none;
  border-top-right-radius: 2px;
  border-bottom-right-radius: 2px;
}
.btn-toolbar > .btn-group {
  float: none;
  display: inline-block;
}
.btn-toolbar > * {
  margin-right: 0;
  margin-left: 5px;
}
.btn-toolbar > *:first-child {
  margin-left: 0;
}
